<template>
  <div class="m-footer" v-if="isShow">
    <van-tabbar v-model="active" active-color="#000" inactive-color="#000">
      <van-tabbar-item
        v-for="(item, index) in tabbarList"
        :key="index"
        replace
        @click="tab(item)"
      >
        <span>{{ item.name }}</span>
        <template #icon="props">
          <img :src="props.active ? item.active : item.inactive" />
        </template>
      </van-tabbar-item>
    </van-tabbar>
  </div>
</template>

<script>
import App from "@/utils/app/index";
export default {
  /*   props: {
    active: {
      type: Number,
      default: 0,
    },
  }, */
  data() {
    return {
      active: 0,
      isShow: true,
      tabbarList: [
        {
          name: "首页",
          url: "/",
          active:
            "data:image/png;base64,",
          inactive:
            "data:image/png;base64,",
        },
        {
          name: "版权品",
          url: "/market?type=copyright",
          active:
            "data:image/png;base64,",
          inactive:
            "data:image/png;base64,",
        },
        {
          name: "衍生品",
          url: "/market?type=derive",
          active:
            "data:image/png;base64,",
          inactive:
            "data:image/png;base64,",
        },
        {
          name: "资讯",
          url: "/hotspot",
          active:
            "data:image/png;base64,",
          inactive:
            "data:image/png;base64,",
        },
        {
          name: "我的",
          url: "/mine",
          active:
            "data:image/png;base64,",
          inactive:
            "data:image/png;base64,",
        },
      ],
      icon: {},
    };
  },
  watch: {
    $route: function (to, from) {
      // console.log(to.query.type);

      this.checkFooterStatus(to.meta);
      this.createActive(to.query.type, to.meta.tab);
    },
  },
  created() {
    this.createActive(this.$route.query.type, this.$route.meta.tab);
    this.checkFooterStatus(this.$route.meta);
  },
  methods: {
    createActive(type, tab) {
      if (type) {
        if (type == "derive") {
          this.active = 2;
        } else if (type == "copyright") {
          this.active = 1;
        } else {
          this.active = Number(type);
        }
      } else {
        this.active = tab;
      }
    },
    checkFooterStatus(meta) {
      const browser = App.browser.browser;

      this.isShow = true;
      if (meta.hideMoblieFooter) {
        this.isShow = false;
      }
      if (App.browser.theone && App.browser.theone.isTheOne) {
        //app内
        if (meta.footerTheoneHide) {
          this.isShow = false;
        }
      }
      if (browser.wechat) {
        //微信内
        if (meta.footerWechatHide) {
          this.isShow = false;
        }
      }
      if (browser.weibo) {
        //微博内
        if (meta.footerWeiboHide) {
          this.isShow = false;
        }
      }
    },
    tab(info) {
      this.$router.push(info.url);
    },
  },
};
</script>

